import yaml


def process_json(data, indent=0):
    """
    Convert JSON data to a string without enclosing curly braces.
    
    Args:
    - data: Dictionary containing JSON data.
    - indent: Indentation level (default is 0).
    
    Returns:
    - JSON-like string without curly braces.
    """
    result = ""
    for key, value in data.items():
        if isinstance(value, dict):
            result += "  " * indent + f"{key}:\n"
            result += process_json(value, indent + 1)
        else:
            result += "  " * indent + f"{key}: {value}\n"
            
    return result


def interpolate(config, environment):
    for key, value in config.items():
        if isinstance(value, str):
            config[key] = value.format(**environment)
        elif isinstance(value, dict):
            interpolate(value)


def load_yaml_with_environment(filename, environment):
    with open(filename, 'r') as file:
        config = yaml.safe_load(file)

    interpolate(config, environment)
    return config

