from jubollm.base_generator import BaseGenerator


class BaseResponseGenerator(BaseGenerator):
    def __init__(self, llm_config):
        super().__init__(**llm_config)

    def _construct_patch_mappings(self, user_input, query_result, collection_name):
        """Constructs patch mappings specific to the generator type.

        This method must be implemented by subclasses.
        """
        raise NotImplementedError("Subclasses must implement this method.")

    def generate_response(self, user_input, query_result, collection_name):
        patch_mappings = self._construct_patch_mappings(user_input, query_result, collection_name)
        response = self.generate(**patch_mappings)
        return response
