from jubollm.nCopilot.generator.base_args_generator import BaseArgsGenerator


class NisArgsGenerator(BaseArgsGenerator):
    def _construct_patch_mappings(self, user_input, collection_name, columns_description=None):
        if collection_name == 'patients_info':
            from jubollm.nCopilot.generator.prompts.nis_orgchat_patients import BASIC_PROMPT_JSON, TOOLS_JSON
        else:
            from jubollm.nCopilot.generator.prompts.nis_orgchat_vitalsigns import BASIC_PROMPT_JSON, TOOLS_JSON
        
        return {
            'tools': TOOLS_JSON,
            'user_input': user_input,
            'base_prompt': BASIC_PROMPT_JSON
        }
