from jubollm.nCopilot.searcher.base_searcher import BaseSearcher


class MongoCollectionSearcher(BaseSearcher):
    def __init__(self, db=None):
        super().__init__(db)

    # 這邊長久之計不能這樣寫
    def search(self, query):
        if any(keyword in query for keyword in ["血壓", "脈搏", "體溫", "血氧"]):
            return "vitalsigns"
        elif any(keyword in query for keyword in ["生日", "身高", "體重", "醫院", "血型"]):
            return "patients_info"
        else:
            return "default"
